#include <iostream>
#include <stdlib.h>
#include <time.h>
using namespace std;
//希尔排序
void ShellSort(int r[], int n)
{
    int i,j,d;
    int temp;
    for (d=n/2; d>=1; d=d/2) {
        for (i=d+1; i<=n; i++){
            temp = r[i];
            for (j=i-d; j>0 && r[0]<r[j]; j=j-d)
                r[j+d]=r[j];
            r[j+d] = temp;
        }
    }
}

//快速排序
int Partition(int r[], int first, int end)
{
    int i = first;
    int j = end;
    int temp = r[i];
    while(i<j)
    {
        while(j>i && r[j]>=temp)
            j--;
        r[i] = r[j];
        while(i<j && r[i]<=temp)
            i++;
        r[j] = r[i];
    }
    r[i] = temp;
    return i;
}
void QuickSort(int r[], int first, int end)
{
    if (first<end)
    {
       int pivot = Partition(r, first, end);
       QuickSort(r, first, pivot-1);
       QuickSort(r, pivot+1, end);
    }
}
void Print(int r[])
{
    int i = 0;
    while(r[i])
    {
        cout << r[i] << "、";
        i++;
    }
}
